import React from 'react';
import { makeStyles } from '@material-ui/core/styles';
import Card from '@material-ui/core/Card';
import Adobe from '../../../Assets/Imgs/adobe.jpg'
import Save from '../../../Assets/Imgs/save.svg'
import Discussion from '../../../Assets/Imgs/discussion.svg'
import DownloadIcon from '../../../Assets/Imgs/downloadIcon.svg'
import Calender from '../../../Assets/Imgs/calender.svg'
import CardMedia from '@material-ui/core/CardMedia';

const useStyles = makeStyles({
    root: {
        width: '100%',
        height: '600px',
        borderRadius: 'unset'
    },

    media: {
        height: '100%'
    },

    Main: {
        display: 'flex',
        width: '95%',
        margin: 'auto',
        justifyContent: 'space-between'
    },

    innerDiv: {
        display: 'flex',

    },
    contentDiv: {
        display: 'flex',
        backgroundColor: '#d5f5ef',
        borderRadius: '5px',
        padding: '6px 17px',
    },

    para: {
        margin: 'auto 10px',
        color: '#5ED0BC',
        fontWeight: 'bold',
        fontSize: '12px'
    }

});

export default function BottomPart() {
    const classes = useStyles();

    return (
        <>
            <Card className={classes.root}>

                <CardMedia
                    className={classes.media}
                    image={Adobe}
                    title="image"
                />

            </Card>

            <div className={classes.Main}>
                <div className={classes.innerDiv} >
                    <div className={classes.contentDiv}>
                        <img src={Save} />
                        <p className={classes.para}>Save note</p>
                    </div>
                    <div className={classes.contentDiv} style={{ backgroundColor: "#FEF9E5", marginLeft: '15px' }}>
                        <img src={Discussion} />
                        <p className={classes.para} style={{ color: "#F68C20" }} >Discussion</p>
                    </div>
                    <div className={classes.contentDiv} style={{ backgroundColor: '#FEEFEE', marginLeft: '15px' }}>
                        <img src={Calender} />
                        <p className={classes.para} style={{ color: '#F56962' }}>Add to calendar</p>
                    </div>
                    <div className={classes.contentDiv} style={{ backgroundColor: '#dedcff', marginLeft: '15px' }}>
                        <img src={DownloadIcon} />
                        <p className={classes.para} style={{ color: '#6C63FF' }}>Download</p>
                    </div>

                </div>
                <div className={classes.innerDiv}>

                </div>
            </div>

        </>
    );
}
